import React, { useEffect } from "react";
import { useDispatch, useSelector } from 'react-redux';
import * as actions from '../action/totalstats.action'
import { Row, Container, Col } from "shards-react";
import shortid from "shortid";
import PageHeader from '../component/pageTitle/pageheader.component';
import TotalStats from '../component/statistics/totalStats.component';
import CountryStat from '../component/statistics/countryStats.component';


const DefaultLayout = () => {

    const dispatch = useDispatch();

    useEffect(() => {
        dispatch(actions.fetch())
    }, [dispatch])

    const { totalFigure } = useSelector(state => ({
        totalFigure: state.TotalStats.totalFigure
    }));

    return (
        <>
            <Container fluid className="main-content-container px-4 container-fluid">
                <Row noGutters className="page-header py-4">
                    <PageHeader title="Covid-19 Quick Stats" subtitle="Dashboard" className="text-sm-left mb-3" />
                </Row>

                {totalFigure.map((stat) => (

                    <Row key= {shortid}>
                        <Col className="col-lg mb-4">
                            <TotalStats
                                id={'small-stats-one'}
                                variation="1"
                                label="Confirmed Cases"
                                value={stat.confirmed}
                            />
                        </Col>
                        <Col className="col-lg mb-4">
                            <TotalStats
                                id={'small-stats-two'}
                                variation="1"
                                label="Recovered Cases"
                                value={stat.recovered}
                            />
                        </Col>
                        <Col className="col-lg mb-4">
                            <TotalStats
                                id={'small-stats-three'}
                                variation="1"
                                label="Critical Cases"
                                value={stat.critical}
                            />
                        </Col>
                        <Col className="col-lg mb-4">
                            <TotalStats
                                id={'small-stats-four'}
                                variation="1"
                                label="Deaths"
                                value={stat.deaths}
                            />
                        </Col>

                    </Row>
                ))}

            </Container>
        </>
    );
}
export default DefaultLayout;
