import React from 'react';
import DataSet from '@antv/data-set';
import { Chart } from '@antv/g2';
import { CardHeader, CardBody, Card } from "shards-react";
import axios from 'axios'

const configuration = {

  "headers": {
      "content-type": "application/octet-stream",
      "x-rapidapi-host": "covid-19-data.p.rapidapi.com",
      "x-rapidapi-key": "78f0398aa4msh86be5ac1359b0f1p1838a7jsnad282c6e4e48"
  }
}

const countryName = [

  'Russia',
  'China',
  'Japan',
  'South Korea',
  'Canada',
  'United Kingdom',
  'United States of America',
  'Brazil',
  'Argentina',
  'Nigeria',
  'France',
  'Germany',
  'Ukraine',
  'Egypt',
  'South Africa',
  'India',
  'Australia',
  'Saudi Arabia',
  'Afghanistan',
  'Spain',
  'Italy',
];

const userData = [

  { name: 'Russia', value: 86.8 },
  { name: 'China', value: 106.3 },
  { name: 'Japan', value: 94.7 },
  { name: 'Mongolia', value: 98 },
  { name: 'Canada', value: 98.4 },
  { name: 'United Kingdom', value: 97.2 },
  { name: 'United States of America', value: 98.3 },
  { name: 'Brazil', value: 96.7 },
  { name: 'Argentina', value: 95.8 },
  { name: 'Algeria', value: 101.3 },
  { name: 'France', value: 94.8 },
  { name: 'Germany', value: 96.6 },
  { name: 'Ukraine', value: 86.3 },
  { name: 'Egypt', value: 102.1 },
  { name: 'South Africa', value: 101.3 },
  { name: 'India', value: 107.6 },
  { name: 'Australia', value: 99.9 },
  { name: 'Saudi Arabia', value: 130.1 },
  { name: 'Afghanistan', value: 106.5 },
  { name: 'Kazakhstan', value: 93.4 },
  { name: 'Indonesia', value: 101.4 },

];

class CountryStats extends React.Component {
  constructor(props) {
    super(props);

    this.state = { geoData: [] , countryData: []};
  }

  async componentDidMount() {


    const response = await axios.get('https:/g2.antv.vision/en/examples/data/world.geo.json');
    const json = await response.data;
    this.setState({geoData: json})

    const ds = new DataSet();
    const dv = ds.createView('back').source(this.state.geoData, {
      type: 'GeoJSON',
    });

   this.setState({countryData:userData });

   console.log(this.state)

    const userDv = ds
      .createView()
      .source(this.state.countryData)
      .transform({
        geoDataView: dv,
        field: 'name',
        type: 'geo.centroid',
        as: ['longitude', 'latitude'],
      });

    const chart = new Chart({
      container: 'container',
      autoFit: true,
      height: 500,
    });

    chart.scale({
      longitude: {
        sync: true,
      },
      latitude: {
        sync: true,
      },
    });
    chart.axis(false);

    chart.legend({ position: 'right' });
    chart.tooltip({
      showTitle: false,
      showMarkers: false
    });

    const bgView = chart.createView();
    bgView.data(dv.rows);
    bgView.tooltip(false);
    bgView
      .polygon()
      .position('longitude*latitude')
      .color('#ebedf0')
      .style({
        lineWidth: 1,
        stroke: '#fafbfc',
      });

    const userView = chart.createView();
    userView.data(userDv.rows);
    userView
      .point()
      .position('longitude*latitude')
      .color('#1890ff')
      .shape('circle')
      .size('value', [5, 15])
      .style({
        lineWidth: 1,
        stroke: '#1890ff',
      });
    userView.interaction('element-active');
    chart.render();

  };

  render() {

    return (
      <>
        <Card small className="h-100">
          <CardHeader className="border-bottom">
            <h6 className="m-0">{this.props.title}</h6>
          </CardHeader>
          <CardBody className="pt-0">
            <div id="container" />
          </CardBody>
        </Card>
      </>
    );
  }

}
export default CountryStats;
