import React from 'react';
import classNames from 'classnames';
import { Col, Row } from 'shards-react';
import TotalStats from '../statistics/totalStats.component'


const Pageheader = (props) => {

    const classes = classNames(
        props.className,
        "text-center",
        "text-md-center",
        "mb-sm-0"
    );

    return (
        <>
            <Row>
                <Col xs="12" sm="12" className={classes}>
                    <span className="text-uppercase page-subtitle">{props.subtitle}</span>
                    <h3 className="page-title">{props.title}</h3>
                </Col>
            </Row>
            {props.totalFigure && props.totalFigure.length > 0 && (
                <Row>
                    <Col className="col-lg mb-4">
                        <TotalStats
                            id={'small-stats-one'}
                            variation="1"
                            label="Total Cases"
                            value={props.totalFigure.total_cases}
                        />
                    </Col>
                    <Col className="col-lg mb-4">
                        <TotalStats
                            id={'small-stats-two'}
                            variation="1"
                            label="Total Deaths"
                            value={props.totalFigure.total_deaths}
                        />
                    </Col>
                    <Col className="col-lg mb-4">
                        <TotalStats
                            id={'small-stats-three'}
                            variation="1"
                            label="Total Recovered"
                            value={props.totalFigure.total_recovered}
                        />
                    </Col>
                    <Col className="col-lg mb-4">
                        <TotalStats
                            id={'small-stats-four'}
                            variation="1"
                            label="New Cases"
                            value={props.totalFigure.new_cases}
                        />
                    </Col>
                    <Col className="col-lg mb-4">
                        <TotalStats
                            id={'small-stats-four'}
                            variation="1"
                            label="New Deaths"
                            value={props.totalFigure.new_deaths}
                        />
                    </Col>
                </Row>
            )}

        </>
    );
}

export default Pageheader;
