import api from "./api"

export const ACTION_TYPES = {
    GET: "GET"
}

export const fetch = (countryName) => dispatch => {

    api.CountryStats().get(countryName)
    .then(response => {
        dispatch({
            type: ACTION_TYPES.FETCH,
            payload: response.data
        })
    })
    .catch(err => console.log(err))
}
