import api from "./api"


export const ACTION_TYPES = {
    FETCH: "FETCH",
    FETCHDVDATA: "FECTHDVDATA"
}

export const fetch = (countryName) => dispatch => {

    api.CountryStats().get(countryName)
    .then(response => {
        dispatch ({
            type: ACTION_TYPES.FETCH,
            payload: response.data
        })
    })
    .catch(err => console.log(err))
}

export const fetchDvData = () => async dispatch => {
     await api.CountryStats().fetch()
    .then(response => {
        dispatch ({
            type: ACTION_TYPES.FETCHDVDATA,
            payload: response.data
        })
    })
    .catch(err => console.log(err))
}
