import React from 'react';
import DataSet from '@antv/data-set';
import { Chart } from '@antv/g2';
import { CardHeader, CardBody, Card } from "shards-react";
import axios from 'axios'

const configuration = {

  "headers": {
    "content-type": "application/octet-stream",
    "x-rapidapi-host": "covid-19-data.p.rapidapi.com",
    "x-rapidapi-key": "78f0398aa4msh86be5ac1359b0f1p1838a7jsnad282c6e4e48"
  }
}

const countryName = [

  'Russia',
  'China',
  'Japan',
  'South Korea',
  'Canada',
  'United Kingdom',
  'United States of America',
  'Brazil',
  'Argentina',
  'Nigeria',
  'France',
  'Germany',
  'Ukraine',
  'Egypt',
  'South Africa',
  'India',
  'Australia',
  'Saudi Arabia',
  'Afghanistan',
  'Spain',
  'Italy',
];

const countryInitialData = [];

// const userData = [
//   { name: 'Russia', value: 86.8 },
//   { name: 'China', value: 106.3 },
//   { name: 'Japan', value: 94.7 },
//   { name: 'Mongolia', value: 98 },
//   { name: 'Canada', value: 98.4 },
//   { name: 'United Kingdom', value: 97.2 },
//   { name: 'United States of America', value: 98.3 },
//   { name: 'Brazil', value: 96.7 },
//   { name: 'Argentina', value: 95.8 },
//   { name: 'Algeria', value: 101.3 },
//   { name: 'France', value: 94.8 },
//   { name: 'Germany', value: 96.6 },
//   { name: 'Ukraine', value: 86.3 },
//   { name: 'Egypt', value: 102.1 },
//   { name: 'South Africa', value: 101.3 },
//   { name: 'India', value: 107.6 },
//   { name: 'Australia', value: 99.9 },
//   { name: 'Saudi Arabia', value: 130.1 },
//   { name: 'Afghanistan', value: 106.5 },
//   { name: 'Kazakhstan', value: 93.4 },
//   { name: 'Indonesia', value: 101.4 },
// ];
// date: "1902/04/19"
// location: "Quezaltenango and San Marcos, Guatemala"
// lat: "14"
// lng: "-91"
// deaths: "2000"
// magnitude: "7.5"
// comments: "↵This quake also caused damage


class CountryStats extends React.Component {
  constructor(props) {
    super(props);

    this.state = { geoData: [], countryData: [] };
  }


  loadData = async (callback) => {

    const response = await axios.get('https:/g2.antv.vision/en/examples/data/world.geo.json');
    const json = await response.data;

    countryName.map(async country => {
      const countryResponse = await axios.get('https://covid-19-data.p.rapidapi.com/country?name=' + country, configuration);
      const countryJson = countryResponse.data;
      countryInitialData.push(countryJson[0]);

      if (countryInitialData && countryInitialData.length == countryName.length) {
        callback(countryInitialData, json)
      }
    });
  }

  componentDidMount = async () => {
    this.loadData((data, json) => {
      this.setState({ countryData: data, geoData: json });
      console.log(data)
      this.renderChart();
    })

    // const response = await axios.get('https:/g2.antv.vision/en/examples/data/world.geo.json');
    // const json = await response.data;

    // this.setState({ countryData: countryInitialData, geoData: json })
    // this.renderChart();
  };

  renderChart = () => {

    const chart = new Chart({
      container: 'container',
      autoFit: true,
      height: 500,
      padding: [0, 20, 40]
    });

    chart.scale({
      x: { sync: true },
      y: { sync: true }
    });
    chart.coordinate('rect').reflect('y');
    chart.legend(false);
    chart.axis(false);

    chart.tooltip({
      showTitle: false,
      shared: true,
      showMarkers: false,
      containerTpl: '<div class="g2-tooltip"><table class="g2-tooltip-list"></table></div>',
      itemTpl: '<tr data-index="{index}"><td style="padding:5px;background-color:#852323; color:#fff;">{name}</td><td style="padding:5px;background-color:#fff;color:#000;">{value}</td></tr>',
      domStyles: {
        'g2-tooltip': {
          borderRadius: '2px',
          backgroundColor: '#DDDDDD',
          padding: 0,
          border: '1px solid #333'
        }
      }
    });
    const ds = new DataSet();
    // draw the map
    const dv = ds.createView('back')
      .source(this.state.geoData, {
        type: 'GeoJSON'
      })
      .transform({
        type: 'geo.projection',
        projection: 'geoMercator',
        as: ['x', 'y', 'centroidX', 'centroidY']
      });

    const bgView = chart.createView();
    bgView.data(dv.rows);
    bgView.tooltip(false);
    bgView.polygon()
      .position('x*y')
      .style({
        fill: '#DDDDDD',
        stroke: '#b1b1b1',
        lineWidth: 0.5,
        fillOpacity: 0.85
      });

    const userData = ds.createView().source(this.state.countryData);
    userData.transform({
      type: 'map',
      callback: obj => {
        const projectedCoord = dv.geoProjectPosition([obj.longitude * 1, obj.latitude * 1], 'geoMercator');
        obj.x = projectedCoord[0];
        obj.y = projectedCoord[1];
        obj.deaths = obj.deaths * 1;
        obj.magnitude = obj.critical * 1;
        return obj;
      }
    });

    const pointView = chart.createView();
    pointView.data(userData.rows);
    pointView.point()
      .position('x*y')
      .size('deaths', [2, 30])
      .shape('circle')
      .color('#FF2F29')
      .tooltip('country*confirmed*recovered*critical*deaths')
      .style({
        fillOpacity: 0.45,
      })
      .state({
        active: {
          style: {
            lineWidth: 1,
            stroke: '#FF2F29'
          }
        }
      });
    pointView.interaction('element-active');
    chart.render();

  }

  render() {

    return (

      <>
        <Card small className="h-100">
          <CardHeader className="border-bottom">
            <h6 className="m-0">{this.props.title}</h6>
          </CardHeader>
          <CardBody className="pt-0">
            <div id="container" />

          </CardBody>
        </Card>
      </>
    );
  }

}
export default CountryStats;
