import api from "./api"


export const ACTION_TYPES = {
    GET: "GET",
    FETCH: "FECTH"
}

export const fetch = (countryName,onSuccess) => () => {

    api.CountryStats().get(countryName)
    .then(response => {
        onSuccess(response.data)
    })
    .catch(err => console.log(err))
}

export const fetchDvData = (onSuccess) =>  async dispatch => {
    await api.CountryStats().fetch()
    .then(response => {
        dispatch ({
            type: ACTION_TYPES.FETCH,
            payload: response.data
        })
        return onSuccess(response.data)
    })
    .catch(err => console.log(err))
}
