import api from "./api"

export const ACTION_TYPES ={
    GET:"GET"
}

export const fetch = (countryName, onSuccess) => dispatch => {
    console.log(countryName)
     api.CountryStats().get(countryName)
    .then( response => {
        return (onSuccess(response.data))
    })
    .catch(err => console.log(err))
}
