import React, { useState } from 'react';
import { Card, CardHeader, CardBody, Button, InputGroup, FormInput, InputGroupAddon } from "shards-react";
import { Chart } from '@antv/g2';

const initialState = {
    searchInput: []
}

// const countryCaseData = [
//     { latitude: "9.081999" },
//     { longitude: "8.675277" },
//     { country: "Nigeria" },
//     { recovered: 51 },
//     { critical: 2 },
//     { totalDeaths: 7 },
//     { totalCases: 288 },
//     { active: 230 }

// ]
// const chartData = [
//     { item: '事例一', count: 40, percent: 0.4 },
//     { item: '事例二', count: 21, percent: 0.21 },
//     { item: '事例三', count: 17, percent: 0.17 },
//     { item: '事例四', count: 13, percent: 0.13 },
//     { item: '事例五', count: 9, percent: 0.09 },
//   ];
// const chartData = [
//     { item: "Active", count: 79, percent: 0.79 },
//     { item: "Critical", count: 1, percent: 0.01 },
//     { item: "Recovered", count: 17, percent: 0.17 },
//     { item: "Deaths", count: 2, percent: 0.02 }
// ]

const CountryCaseStats = (props) => {

    const [state, setstate] = useState(initialState)

    const chartData = [];
    let rendered = false;

    const displayChart = (e) => {
        e.preventDefault();
         var filterResult = props.countryCaseData.filter(data => state.searchInput == data.country);
        // var filterResult = countryCaseData;

        if (filterResult && filterResult.length > 0) {

            const percentageActive = ((filterResult[0].active / filterResult[0].totalCases)).toFixed(2);
            const percentageCritical = (((filterResult[0].critical / filterResult[0].totalCases)).toFixed(2))
            const percentageRecovered = (((filterResult[0].recovered / filterResult[0].totalCases))).toFixed(2)
            const percentageDeaths = (((filterResult[0].totalDeaths / filterResult[0].totalCases))).toFixed(2)

            chartData.push({
                item: "Active", 
                count: ~~(percentageActive * 100),
                percent: percentageActive
            });

            chartData.push({
                item: "Critical",
                 count: ~~(percentageCritical * 100),
                 percent: percentageCritical
            });

            chartData.push({
                item: "Recovered", 
                count: ~~(percentageRecovered * 100),
                percent:percentageRecovered
            });

            chartData.push({
                item: "Deaths", 
                count: ~~(percentageDeaths * 100),
                percent: percentageDeaths
            })


            console.log(chartData);

            const chart = new Chart({
                container: 'containerPieChart',
                autoFit: true,
                height: 500,
            });

            chart.data(chartData);
            chart.scale('percent', {
                formatter: (val) => {
                    val = val * 100 + '%';
                    return val;
                },
            }); chart.coordinate('theta', {
                radius: 0.75,
                innerRadius: 0.6,
            });
            chart.tooltip({
                showTitle: false,
                showMarkers: false,
                itemTpl: '<li class="g2-tooltip-list-item"><span style="background-color:{color};" class="g2-tooltip-marker"></span>{name}: {value}</li>',
            });
            chart.annotation()
                .text({
                    position: ['50%', '50%'],
                    content: "Total Confirmed Cases",
                    style: {
                        fontSize: 14,
                        fill: '#8c8c8c',
                        textAlign: 'center',
                    },
                    offsetY: -20,
                })
                .text({
                    position: ['50%', '50%'],
                    content: "Total",
                    style: {
                        fontSize: 20,
                        fill: '#8c8c8c',
                        textAlign: 'center',
                    },
                    offsetX: -10,
                    offsetY: 20,
                });
            chart.interval()
                .adjust('stack')
                .position('percent')
                .color('item')
                .label('percent', (percent) => {
                    return {
                        content: (data) => {
                            return `${data.item}: ${percent * 100}%`;
                        },
                    };
                })
                .tooltip('item*percent', (item, percent) => {
                    percent = percent * 100 + '%';
                    return {
                        name: item,
                        value: percent,
                    };
                });
            rendered = true;
            chart.interaction('element-active');
            chart.render();
        }

    }
    const handleChange = (e) => {
        setstate({ ...state, searchInput: e.target.value })
    }

    return (
        <Card small className="h-100">
            <CardHeader className="border-bottom">
                <h6 className="m-0">{props.title}</h6>
            </CardHeader>
            <CardBody className="py-0">
                <InputGroup seamless className="mb-3">
                    <FormInput placeholder="Enter Country Name" onChange={handleChange} />
                    <InputGroupAddon type="append">
                        <Button theme="primary" onClick={displayChart}>Search</Button>
                    </InputGroupAddon>
                </InputGroup>
                {rendered && (
                    <h3 className="m-0">No Search Record Found</h3>
                )}
                <div id="containerPieChart" />
            </CardBody>

        </Card>
    );
}

export default CountryCaseStats;
