import api from "./api"


export const ACTION_TYPES = {
    GET: "GET",
    FETCH: "FECTH"
}

export const fetch = (countryName,onSuccess) => () => {

    api.CountryStats().get(countryName)
    .then(response => {
        onSuccess(response.data)
    })
    .catch(err => console.log(err))
}

export const fetchDvData = () => async dispatch => {
   const response =  api.CountryStats().fetch()
    const data = await response.json();
    return dispatch ({
        type: ACTION_TYPES.FETCH,
        payload: data
    })
}
