import axios from 'axios'

const baseUrl = 'https://covid-19-data.p.rapidapi.com/'

const configuration = {

    "headers": {
        "content-type": "application/octet-stream",
        "x-rapidapi-host": "covid-19-data.p.rapidapi.com",
        "x-rapidapi-key": "78f0398aa4msh86be5ac1359b0f1p1838a7jsnad282c6e4e48"
    }
}

export default {

    TotalStats(url = baseUrl + 'totals', config = configuration) {
        return {
            get: () => axios.get(url, config)
        }
    },
    CountryStats(url = baseUrl + 'country', config = configuration) {
        return {
            get: (countryName) => axios.get(url + '?name=' + countryName, config) ,
            fetch: () => axios.get('https:/g2.antv.vision/en/examples/data/world.geo.json') 
        }

    }
}
