import React, { useState } from 'react';
import { Card, CardHeader, CardBody, Button, InputGroup, FormInput, InputGroupAddon } from "shards-react";
import { Chart } from '@antv/g2';

const initialState = {
    searchInput: []
}

const CountryCaseStats = (props) => {

    const [state, setstate] = useState(initialState)

    const chartData = [];
    let rendered = false;

    const displayChart = (e) => {
        e.preventDefault();
        var filterResult = props.countryCaseData.filter(data => state.searchInput == data.country);

        if (filterResult && filterResult.length > 0) {

            chartData.push({
                item: "Active", count: filterResult[0].active / filterResult[0].totalCases
                , percentage: ((filterResult[0].active / filterResult[0].totalCases) * 100).toFixed(1)
            });

            chartData.push({
                item: "Critical", count: filterResult[0].critical / filterResult[0].totalCases
                , percentage: ((filterResult[0].critical / filterResult[0].totalCases) * 100).toFixed(1)
            });

            chartData.push({
                item: "Recovered", count: filterResult[0].recovered / filterResult[0].totalCases
                , percentage: ((filterResult[0].recovered / filterResult[0].totalCases) * 100).toFixed(1)
            });

            chartData.push({
                item: "Deaths", count: filterResult[0].totalDeaths / filterResult[0].totalCases
                , percentage: ((filterResult[0].totalDeaths / filterResult[0].totalCases) * 100).toFixed(1)
            })

            const chart = new Chart({
                container: 'container',
                autoFit: true,
                height: 500,
            });

            chart.data(chartData);
            chart.scale('percent', {
                formatter: (val) => {
                    val = val * 100 + '%';
                    return val;
                },
            }); chart.coordinate('theta', {
                radius: 0.75,
                innerRadius: 0.6,
            });
            chart.tooltip({
                showTitle: false,
                showMarkers: false,
                itemTpl: '<li class="g2-tooltip-list-item"><span style="background-color:{color};" class="g2-tooltip-marker"></span>{name}: {value}</li>',
            });
            chart.annotation()
                .text({
                    position: ['50%', '50%'],
                    content: "Total Confirmed Cases",
                    style: {
                        fontSize: 14,
                        fill: '#8c8c8c',
                        textAlign: 'center',
                    },
                    offsetY: -20,
                })
                .text({
                    position: ['50%', '50%'],
                    content: filterResult[0].totalCases,
                    style: {
                        fontSize: 20,
                        fill: '#8c8c8c',
                        textAlign: 'center',
                    },
                    offsetX: -10,
                    offsetY: 20,
                });
            chart.interval()
                .adjust('stack')
                .position('percent')
                .color('item')
                .label('percent', (percent) => {
                    return {
                        content: (data) => {
                            return `${data.item}: ${percent * 100}%`;
                        },
                    };
                })
                .tooltip('item*percent', (item, percent) => {
                    percent = percent * 100 + '%';
                    return {
                        name: item,
                        value: percent,
                    };
                });
            rendered = true;
            chart.interaction('element-active');
            chart.render();
        }

    }
    const handleChange = (e) => {
        setstate({ ...state, searchInput: e.target.value })
    }

    return (
        <Card small className="h-100">
            <CardHeader className="border-bottom">
                <h6 className="m-0">{props.title}</h6>
            </CardHeader>
            <CardBody className="py-0">
                <InputGroup seamless className="mb-3">
                    <FormInput placeholder="Enter Country Name" onChange={handleChange} />
                    <InputGroupAddon type="append">
                        <Button theme="primary" onClick={displayChart}>Search</Button>
                    </InputGroupAddon>
                </InputGroup>
                {rendered && (
                    <h3 className="m-0">No Search Record Found</h3>
                )}
                <div id="container" />
            </CardBody>

        </Card>
    );
}

export default CountryCaseStats;
