import api from "./api"


export const ACTION_TYPES = {
    FETCHDVDATA: "FECTHDVDATA"
}

export const fetchDvData = () => dispatch => {
      api.CountryStats().fetch()
    .then(response => {
        dispatch ({
            type: ACTION_TYPES.FETCHDVDATA,
            payload: response.data
        })
    })
    .catch(err => console.log(err))
}
