import api from "./api"


export const ACTION_TYPES = {
    GET: "GET"
}

export const fetch = (onSuccess) => () => {

    const countryData = [];

    const countryName = [
        'Russia',
        'China',
        'Japan',
        'South Korea',
        'Canada',
        'United Kingdom',
        'United States of America',
        'Brazil',
        'Argentina',
        'Nigeria',
        'France',
        'Germany',
        'Ukraine',
        'Egypt',
        'South Africa',
        'India',
        'Australia',
        'Saudi Arabia',
        'Afghanistan',
        'Kazakhstan',
        'Indonesia',
      ];

     countryName.map((country => {

        api.CountryStats().get(country)
        .then(response => {
            countryData.push(response.data)
        })
        .catch(err => console.log(err))
    }));
   return onSuccess(countryData)
}
