import api from "./api"

export const ACTION_TYPES ={
    FETCH : "FETCH",
    GET:"GET"
}

export const fetch = (countryName, onSuccess)=> dispatch => {
      api.CountryStats().get(countryName)
    .then( response => {
        return (onSuccess(response.data))
    })
    .catch(err => console.log(err))
}

export const fetchGeoData = () => dispatch => {
    api.CountryStats().fetch()
    .then(response => response.json())
    .then((data) => {
        dispatch({
            type: ACTION_TYPES.FETCH,
            payload: data
        })
    })
    .catch(err => console.log(err))
}
