import React, { useEffect } from 'react';
import DataSet from '@antv/data-set';
import { Chart } from '@antv/g2';
import { CardHeader, CardBody, Card } from "shards-react";
import { useDispatch, useSelector } from 'react-redux';
import * as action from '../../action/countryStats.action';


const countryName = [
  'Russia',
  'China',
  'Japan',
  'South Korea',
  'Canada',
  'United Kingdom',
  'United States of America',
  'Brazil',
  'Argentina',
  'Nigeria',
  'France',
  'Germany',
  'Ukraine',
  'Egypt',
  'South Africa',
  'India',
  'Australia',
  'Saudi Arabia',
  'Afghanistan',
  'Kazakhstan',
  'Indonesia',
];
const countryData = [];

const CountryStats = ({ title }) => {

  const dispatch = useDispatch()

  const onSuccess = (data) => {
    countryData.push(data)
  }


  dispatch(action.fetchDvData())

  const{geoData} = useSelector( state => ({
        geoData: state.CountryStats.geoData
  }))

  useEffect(() => {

    countryName.map((country => {
      dispatch(action.fetch(country,onSuccess));
    }));

    chart.render();

  }, [dispatch])

  if(countryData.length > 0){
    const ds = new DataSet();
    const dv = ds.createView('back').source(geoData, {
      type: 'GeoJSON',
    });
    const userDv = ds
      .createView()
      .source(countryData)
      .transform({
        geoDataView: dv,
        field: 'confirmed',
        type: 'geo.centroid',
        as: ['longitude', 'latitude'],
      });
    const chart = new Chart({
      container: 'container',
      autoFit: true,
      height: 500,
    });
    chart.scale({
      longitude: {
        sync: true,
      },
      latitude: {
        sync: true,
      },
    });
    chart.axis(false);

    chart.legend({ position: 'right' });
    chart.tooltip({
      showTitle: false,
      showMarkers: false
    });

    const bgView = chart.createView();
    bgView.data(dv.rows);
    bgView.tooltip(false);
    bgView
      .polygon()
      .position('longitude*latitude')
      .color('#ebedf0')
      .style({
        lineWidth: 1,
        stroke: '#fafbfc',
      });

    const userView = chart.createView();
    userView.data(userDv.rows);
    userView
      .point()
      .position('longitude*latitude')
      .color('#1890ff')
      .shape('circle')
      .size('confirmed', [5, 15])
      .style({
        lineWidth: 1,
        stroke: '#1890ff',
      });
    userView.interaction('element-active');
  }

  return (
    <>
      <Card small className="h-100">
        <CardHeader className="border-bottom">
          <h6 className="m-0">{title}</h6>
        </CardHeader>
        <CardBody className="pt-0">
        <div id="container" />
        </CardBody>
      </Card>
    </>
  );
}

export default CountryStats;
