import React, { useEffect } from "react";
import { useDispatch, useSelector } from 'react-redux';
import * as actions from '../action/totalstats.action'
import { Row, Container, Col } from "shards-react";
import shortid from "shortid";
import PageHeader from "../component/pageTitle/pageheader.component";
import TotalStats from "../component/statistics/totalStats.component";
import CountryStat from "../component/statistics/countryStats.component";
import axios from 'axios'


const configuration = {

    "headers": {
        "content-type": "application/octet-stream",
        "x-rapidapi-host": "covid-19-data.p.rapidapi.com",
        "x-rapidapi-key": "78f0398aa4msh86be5ac1359b0f1p1838a7jsnad282c6e4e48"
    }
  }

  const countryName = [

    'Russia',
    'China',
    'Japan',
    'South Korea',
    'Canada',
    'United Kingdom',
    'United States of America',
    'Brazil',
    'Argentina',
    'Nigeria',
    'France',
    'Germany',
    'Ukraine',
    'Egypt',
    'South Africa',
    'India',
    'Australia',
    'Saudi Arabia',
    'Afghanistan',
    'Spain',
    'Italy',
  ];

const initialState = {
    userData: []
}

const countryData = []


const DefaultLayout = () => {

    const [state, setstate] = useState(initialState)
    const dispatch = useDispatch();

    useEffect(() => {
        dispatch(actions.fetch())

        countryName.forEach( async country => {
            const response =  await axios.get('https://covid-19-data.p.rapidapi.com/country?name=' + country , configuration);
            const json =  await response.data;
             countryData.push({name:json[0].country, value:json[0].confirmed });

          });
    }, [dispatch])

   setstate({...state, userData: countryData})

    const { totalFigure } = useSelector(state => ({
        totalFigure: state.TotalStats.totalFigure
    }));

    return (
        <>
            <Container fluid className="main-content-container px-4 container-fluid">
                <Row noGutters className="page-header py-4">
                    <PageHeader title="Covid-19 Quick Stats" subtitle="Dashboard" className="text-sm-left mb-3" />
                </Row>

                {totalFigure.map((stat) => (

                    <Row key= {shortid}>
                        <Col className="col-lg mb-4">
                            <TotalStats
                                id={'small-stats-one'}
                                variation="1"
                                label="Confirmed Cases"
                                value={stat.confirmed}
                            />
                        </Col>
                        <Col className="col-lg mb-4">
                            <TotalStats
                                id={'small-stats-two'}
                                variation="1"
                                label="Recovered Cases"
                                value={stat.recovered}
                            />
                        </Col>
                        <Col className="col-lg mb-4">
                            <TotalStats
                                id={'small-stats-three'}
                                variation="1"
                                label="Critical Cases"
                                value={stat.critical}
                            />
                        </Col>
                        <Col className="col-lg mb-4">
                            <TotalStats
                                id={'small-stats-four'}
                                variation="1"
                                label="Deaths"
                                value={stat.deaths}
                            />
                        </Col>

                    </Row>
                ))}
               <Row>
                    <Col lg="8" md="12" sm="12" className="mb-4">
                        <CountryStat title="Total Confirmed Cases By Country" countryStats={state.userData}/>
                    </Col >
                </Row>
            </Container>
        </>
    );
}
export default DefaultLayout;
