import React from 'react';
import { Card, CardHeader, CardBody, Button, InputGroup, FormInput, InputGroupAddon } from "shards-react";
import { Chart } from '@antv/g2';


const CountryCaseStats = (props) => {

    const chartData = [];
    const rendered = false;

    const displayChart = (e) => {
        e.preventDefault();
        var filterResult = props.countryCaseData.filter(data => e.target.value == data.country);

        if (filterResult && filterResult.length > 0) {

            chartData.push({
                item: "Active", count: filterResult.cases.active / filterResult.cases.total
                , percentage: (filterResult.cases.active / filterResult.cases.total) * 100
            });

            chartData.push({
                item: "Critical", count: filterResult.cases.critical / filterResult.cases.total
                , percentage: (filterResult.cases.critical / filterResult.cases.total) * 100
            });

            chartData.push({
                item: "Recovered", count: filterResult.cases.recovered / filterResult.cases.total
                , percentage: (filterResult.cases.recovered / filterResult.cases.total) * 100
            });

            chartData.push({
                item: "Deaths", count: filterResult.deaths / filterResult.cases.total
                , percentage: (filterResult.deaths / filterResult.cases.total) * 100
            })

            const chart = new Chart({
                container: 'container',
                autoFit: true,
                height: 500,
            });
            chart.data(chartData);
            chart.scale('percent', {
                formatter: (val) => {
                    val = val * 100 + '%';
                    return val;
                },
            }); chart.coordinate('theta', {
                radius: 0.75,
                innerRadius: 0.6,
            });
            chart.tooltip({
                showTitle: false,
                showMarkers: false,
                itemTpl: '<li class="g2-tooltip-list-item"><span style="background-color:{color};" class="g2-tooltip-marker"></span>{name}: {value}</li>',
            });
            chart.annotation()
                .text({
                    position: ['50%', '50%'],
                    content: "Total Confirmed Cases",
                    style: {
                        fontSize: 14,
                        fill: '#8c8c8c',
                        textAlign: 'center',
                    },
                    offsetY: -20,
                })
                .text({
                    position: ['50%', '50%'],
                    content: filterResult.cases.total,
                    style: {
                        fontSize: 20,
                        fill: '#8c8c8c',
                        textAlign: 'center',
                    },
                    offsetX: -10,
                    offsetY: 20,
                });
            chart.interval()
                .adjust('stack')
                .position('percent')
                .color('item')
                .label('percent', (percent) => {
                    return {
                        content: (data) => {
                            return `${data.item}: ${percent * 100}%`;
                        },
                    };
                })
                .tooltip('item*percent', (item, percent) => {
                    percent = percent * 100 + '%';
                    return {
                        name: item,
                        value: percent,
                    };
                });
            rendered = true;
            chart.interaction('element-active');
            chart.render();
        }

    }
    const searchResultDisplay = chartData && Array.isArray(chartData) ? <div id="container" /> : <h3 className="m-0"></h3>

    return (
        <Card small className="h-100">
            <CardHeader className="border-bottom">
                <h6 className="m-0">{props.title}</h6>
            </CardHeader>
            <CardBody className="d-flex py-0">
                <InputGroup seamless className="mb-3">
                    <FormInput placeholder="Enter Country Name" />
                    <InputGroupAddon type="append">
                        <Button theme="primary" onClick={displayChart}>Search</Button>
                    </InputGroupAddon>
                </InputGroup>
                {rendered &&
                    { searchResultDisplay }
                }
            </CardBody>

        </Card>
    );
}

export default CountryCaseStats;
